addtab.handler:
function(){
                var currentEditable = this;
                jQuery.post(
                    this.path + '/*',
                    {
                        'sling:resourceType' : 'videojet-global/components/content/tabs/tab',
                        'jcr:primaryType' : 'nt:unstructured',
                        ':nameHint' : 'tab'
                    },
                    function( data ) { currentEditable.refreshSelf(); }
                );
            }
